update product set prod_cnt = product.prod_cnt - (select a.prod_wearing_cnt from (select prod_id, sum(prod_wearing_cnt) as prod_wearing_cnt from product_wearing where prod_wearing_id in
#{item}
group by prod_id) a where product.prod_id = a.prod_id) where product.prod_id = (select prod_id from product_wearing where prod_wearing_id in
#{item}
group by prod_id) and product.company_id = 'ROOT'

mapper.select:
select t10.prod_wearing_id from product_wearing t10, (select * from product where company_id = 'ROOT') t20 where t10.prod_id = t20.prod_id and t10.prod_wearing_flg = 0 and t10.prod_wearing_id in
#{item}
and t20.prod_cnt - t10.prod_wearing_cnt < 0

mapper.update:
